CREATE TABLE `Teachers` (`id` int unsigned NOT NULL AUTO_INCREMENT,`name` varchar(45),`salary` int unsigned,`age` int unsigned,PRIMARY KEY (`id`));
CREATE TABLE `Courses`(`id` int unsigned NOT NULL AUTO_INCREMENT,`name` varchar(500),`type` enum('DESIGN','PROGRAMMING','MARKETING','MANAGEMENT'),`description` varchar(500),`teacher_id` int unsigned,`price` int unsigned,PRIMARY KEY (`id`),KEY `teacher_idx` (`teacher_id`),CONSTRAINT `teacher` FOREIGN KEY (`teacher_id`) REFERENCES `Teachers` (`id`) ON DELETE SET NULL);
CREATE TABLE `Students`(`id` int unsigned NOT NULL AUTO_INCREMENT,`name` varchar(45),`age` int unsigned,`registration_date` date,PRIMARY KEY (`id`));
CREATE TABLE `Subscriptions` (`student_id` int unsigned,`course_id` int unsigned,`subscription_date` date,UNIQUE KEY `unq` (`student_id`,`course_id`),KEY `course_idx` (`course_id`),CONSTRAINT `course` FOREIGN KEY (`course_id`) REFERENCES `Courses` (`id`) ON DELETE CASCADE,CONSTRAINT `student` FOREIGN KEY (`student_id`) REFERENCES `Students` (`id`) ON DELETE SET NULL);